import{openDB}from"idb";const DATABASE_NAME="restaurant-favorite-db",DATABASE_VERSION=1,OBJECT_STORE_NAME="restaurants",dbPromise=openDB(DATABASE_NAME,1,{upgrade(t){t.objectStoreNames.contains("restaurants")||t.createObjectStore("restaurants",{keyPath:"id"})}}),FavoriteRestaurantIdb={getRestaurant:async t=>t?(await dbPromise).get("restaurants",t):null,getAllRestaurants:async()=>(await dbPromise).getAll("restaurants"),async putRestaurant(t){if(Object.prototype.hasOwnProperty.call(t,"id"))return(await dbPromise).put("restaurants",t)},deleteRestaurant:async t=>(await dbPromise).delete("restaurants",t),async deleteAll(){const t=(await dbPromise).transaction("restaurants","readwrite").objectStore("restaurants");await t.clear()}};export default FavoriteRestaurantIdb;